using QuanLySinhVien.Shared.Enums;using QuanLySinhVien.View.Views.Components.CommonUse;namespace QuanLySinhVien.View.Views.Components.NavList.Dialog;public class CustomDialog : Form{    protected TitleButton _btnLuu;    protected DialogType _dialogType;    private CustomButton _exitButton;    private TableLayoutPanel _mainLayout;    protected TableLayoutPanel _textBoxsContainer;    protected string _title;    public CustomDialog(string title, DialogType dialogType, int width = 400, int height = 600)    {        _title = title;        _dialogType = dialogType;        _textBoxsContainer = new TableLayoutPanel();        this.width = width;        this.height = height;        Init();    }    public int width { get; set; }    public int height { get; set; }    private void Init()    {        Width = width;        Height = height;        BackColor = MyColor.White;        StartPosition = FormStartPosition.CenterScreen;        FormBorderStyle = FormBorderStyle.None;        SuspendLayout();        _mainLayout = new TableLayoutPanel        {            AutoSize = true,            RowCount = 4,            Dock = DockStyle.Fill,            BorderStyle = BorderStyle.FixedSingle        };        _mainLayout.RowStyles.Add(new RowStyle(SizeType.AutoSize));        _mainLayout.RowStyles.Add(new RowStyle(SizeType.AutoSize));        _mainLayout.RowStyles.Add(new RowStyle(SizeType.Percent, 100));        _mainLayout.RowStyles.Add(new RowStyle(SizeType.AutoSize));        SetTopBar();        SetTitleBar();        SetTextBoxsContainer();        SetBottom();        Controls.Add(_mainLayout);        ResumeLayout(false);    }    private void SetTopBar()    {        var panel = new TableLayoutPanel        {            ColumnCount = 2,            Dock = DockStyle.Fill,            AutoSize = true,            Margin = new Padding(0)        };        panel.ColumnStyles.Add(new ColumnStyle(SizeType.Percent, 100));        panel.ColumnStyles.Add(new ColumnStyle(SizeType.AutoSize));        var topTitle = new Label        {            Text = _title,            Anchor = AnchorStyles.Left,            BackColor = MyColor.GrayBackGround,            Dock = DockStyle.Fill,            Margin = new Padding(0)        };        panel.Controls.Add(topTitle);        _exitButton = new CustomButton(25, 25, "exitbutton.svg", MyColor.GrayBackGround, false, false, false, false);        _exitButton.HoverColor = MyColor.GrayHoverColor;        _exitButton.SelectColor = MyColor.GraySelectColor;        _exitButton.Margin = new Padding(0);        _exitButton.Anchor = AnchorStyles.Right;        _exitButton.MouseDown += (sender, args) => Close();        panel.Controls.Add(_exitButton);        _mainLayout.Controls.Add(panel);    }    private void SetTitleBar()    {        var panel = new TableLayoutPanel        {            BackColor = MyColor.MainColor,            Dock = DockStyle.Fill,            AutoSize = true,            Margin = new Padding(0),            Padding = new Padding(0, 10, 0, 10)        };        var lbl = GetTitle();        panel.Controls.Add(lbl);        _mainLayout.Controls.Add(panel);    }    private void SetTextBoxsContainer()    {        _textBoxsContainer.Dock = DockStyle.Fill;        _mainLayout.Controls.Add(_textBoxsContainer);    }    private Label GetTitle()    {        var title = new Label        {            Text = _title,            AutoSize = true,            Dock = DockStyle.Fill,            Anchor = AnchorStyles.None,            Font = GetFont.GetFont.GetMainFont(16, FontType.ExtraBold),            ForeColor = Color.White        };        return title;    }    private void SetBottom()    {        //Thêm có Đặt lại, Lưu, Hủy        var panel = new TableLayoutPanel        {            AutoSize = true,            Dock = DockStyle.Fill,            ColumnCount = 3        };        panel.ColumnStyles.Add(new ColumnStyle(SizeType.Percent, 100));        panel.ColumnStyles.Add(new ColumnStyle(SizeType.AutoSize));        panel.RowStyles.Add(new RowStyle(SizeType.AutoSize));        if (_dialogType == DialogType.Them || _dialogType == DialogType.Sua)        {            panel.Controls.Add(new Panel { Height = 0 });            _btnLuu = new TitleButton("Lưu");            panel.Controls.Add(_btnLuu);            var btnHuy = new TitleButton("Hủy");            btnHuy._mouseDown += () => Close();            panel.Controls.Add(btnHuy);        }        else        {            panel.Controls.Add(new Panel { Height = 0 });            var btnThoat = new TitleButton("Thoát");            btnThoat._mouseDown += () => Close();            panel.Controls.Add(btnThoat, 2, 0);        }        _mainLayout.Controls.Add(panel, 0, 3);    }    private void SetBottomUpdate()    {    }    private void SetBottomDetail()    {    }}