using QuanLySinhVien.Views.Components.CommonUse;using QuanLySinhVien.Views.Enums;namespace QuanLySinhVien.Views.Components.NavList.Dialog;public class CustomDialog : Form{        private CustomButton _exitButton;    private TableLayoutPanel _mainLayout;    public int width { get; set; } = 400;    public int height { get; set; } = 600;    public DialogType _dialogType;     public string _title { get; set; } = "Chưa cài đặt";        public CustomDialog()    {        Init();    }        void Init()    {        Width = width;        Height = height;        BackColor = MyColor.White;        StartPosition = FormStartPosition.CenterScreen;        this.FormBorderStyle = FormBorderStyle.None;        this.SuspendLayout();                _mainLayout = new TableLayoutPanel        {            AutoSize = true,            RowCount = 4,            Dock = DockStyle.Fill,            BorderStyle = BorderStyle.FixedSingle,            CellBorderStyle = TableLayoutPanelCellBorderStyle.Single        };                _mainLayout.RowStyles.Add(new RowStyle(SizeType.AutoSize));        _mainLayout.RowStyles.Add(new RowStyle(SizeType.AutoSize));        _mainLayout.RowStyles.Add(new RowStyle(SizeType.Percent, 100));        _mainLayout.RowStyles.Add(new RowStyle(SizeType.AutoSize));        SetTopBar();        SetTitleBar();        SetButtonBottom();                this.Controls.Add(_mainLayout);        this.ResumeLayout(false);    }    void SetTopBar()    {        TableLayoutPanel panel = new TableLayoutPanel        {            ColumnCount = 2,            Dock = DockStyle.Fill,            AutoSize = true,            Margin = new Padding(0),        };                panel.ColumnStyles.Add(new ColumnStyle(SizeType.Percent, 100));        panel.ColumnStyles.Add(new ColumnStyle(SizeType.AutoSize));        Panel emtyBox = new Panel { BackColor = MyColor.GrayBackGround, Dock = DockStyle.Fill , Height = 25};        emtyBox.BackColor = MyColor.GrayBackGround;        emtyBox.Margin = new Padding(0);        panel.Controls.Add(emtyBox);                _exitButton = new CustomButton(25, 25, "exitbutton.svg", MyColor.GrayBackGround, false, false, false, false);        _exitButton.HoverColor = MyColor.GrayHoverColor;        _exitButton.SelectColor = MyColor.GraySelectColor;        _exitButton.Margin = new Padding(0);        _exitButton.Anchor = AnchorStyles.Right;                _exitButton.MouseDown +=  (sender, args) => this.Close();         panel.Controls.Add(_exitButton);                this._mainLayout.Controls.Add(panel);    }    void SetTitleBar()    {        TableLayoutPanel panel = new TableLayoutPanel        {            BackColor = MyColor.MainColor,            Dock = DockStyle.Fill,            AutoSize = true,            Margin = new Padding(0),        };        Label lbl = GetTitle();        panel.Controls.Add(lbl);        this._mainLayout.Controls.Add(panel);    }    Label GetTitle()    {        Label title = new Label        {            Text = _title,            AutoSize = true,            Dock = DockStyle.Fill,            Anchor = AnchorStyles.None,            Font = GetFont.GetFont.GetMainFont(18,  FontType.ExtraBold),            ForeColor = Color.White        };        return title;    }    void SetButtonBottom()    {        //Thêm có Đặt lại, Lưu, Hủy        TitleButton btnDatLai = new TitleButton("Đặt lại");        this._mainLayout.Controls.Add(btnDatLai, 0, 3);    }}